                secret = ${secret},
                scope = ${oauth_scope},
                callback = ${oauth_callback},
                status = ${oauth_client_status},
                client_name = ${name},
                custom = ${custom},
                environment = ${environment}
                WHERE client_key = ${client_key}
                AND client_ident=${client_ident}

